KISSY.add("app/views/util/confirm",function(n,t){return t.extend({tmpl:'<h2 class=h2-ex>{{confirmTitle}}</h2> <p style="margin-bottom: 20px;">{{confirmContent}}</p> <p><a href="javascript:;" class="btn btn-size28 mr10" mx-click=confirm>\u786e\u5b9a</a> <a href="javascript:;" mx-click=cancel>\u53d6\u6d88</a></p>',init:function(n){this.manage("data",n)},render:function(){var n=this,t=n.getManaged("data");n.setViewPagelet({confirmTitle:t.confirmTitle,confirmContent:t.confirmContent})},"confirm<click>":function(){var n=this,t=n.getManaged("data"),a=t.confirmFn;a&&a()},"cancel<click>":function(){var n=this,t=n.getManaged("data"),a=t.cancelFn;a&&a()}})},{requires:["mxext/view","app/models/modelmanager"]});